mapper.select:
SELECT COUNT(*)
FROM REPORT R JOIN BOARD B USING(BOARD_NO) JOIN MEMBER M USING(USER_NO) JOIN UPPER_CATEGORY UC USING (CATEGORY_U_NO)
FROM REPORT R JOIN REPLY RE USING(REPLY_NO) JOIN BOARD B ON RE.REF_BNO = B.BOARD_NO JOIN MEMBER M ON R.REPORT_USER_NO = M.USER_NO JOIN UPPER_CATEGORY UC USING(CATEGORY_U_NO)
WHERE B.REPORT_COUNT > 0
AND B.CATEGORY_U_NO = #{catUNo}
AND B.TITLE LIKE #{searchPattern}

SELECT COUNT(*)
FROM BOARD B
JOIN UPPER_CATEGORY UC USING(CATEGORY_U_NO)
WHERE CATEGORY_U_NO NOT IN(1, 9)

	AND B.CATEGORY_U_NO = #{catUNo}



 	AND B.TITLE LIKE #{searchPattern}

		SELECT COUNT(*)
		FROM BOARD B
        JOIN SUB_CATEGORY SC ON B.CATEGORY_S_NO = SC.CATEGORY_S_NO
		WHERE  B.CATEGORY_U_NO = 1

			AND SC.CATEGORY_S_NO = #{catSNo}



		 	AND B.TITLE LIKE #{searchPattern}

		SELECT COUNT(*)
		FROM BOARD B
        JOIN SUB_CATEGORY SC ON B.CATEGORY_S_NO = SC.CATEGORY_S_NO
		WHERE  B.CATEGORY_U_NO = 9

			AND SC.CATEGORY_S_NO = #{catSNo}



		 	AND B.TITLE LIKE #{searchPattern}

SELECT * FROM BOARD B JOIN UPPER_CATEGORY UC USING(CATEGORY_U_NO) JOIN MEMBER M USING(USER_NO) WHERE CATEGORY_U_NO NOT IN(1, 9)
AND B.CATEGORY_U_NO = #{catUNo}
AND B.TITLE LIKE #{searchPattern}
ORDER BY B.STATUS DESC, B.CREATE_DATE DESC

SELECT B.BOARD_NO, SC.CATEGORY_S_NAME, B.TITLE, M.NICK_NAME, B.CREATE_DATE, B.READ_COUNT, B.STATUS FROM BOARD B JOIN MEMBER M ON B.USER_NO = M.USER_NO JOIN UPPER_CATEGORY UC ON B.CATEGORY_U_NO = UC.CATEGORY_U_NO JOIN SUB_CATEGORY SC ON B.CATEGORY_S_NO = SC.CATEGORY_S_NO WHERE B.CATEGORY_U_NO = 1
AND B.CATEGORY_S_NO = #{catSNo}
AND B.TITLE LIKE #{searchPattern}
ORDER BY B.CREATE_DATE DESC

SELECT B.BOARD_NO, SC.CATEGORY_S_NAME, B.TITLE, M.NICK_NAME, B.CREATE_DATE, B.READ_COUNT, B.STATUS FROM BOARD B JOIN MEMBER M ON B.USER_NO = M.USER_NO JOIN UPPER_CATEGORY UC ON B.CATEGORY_U_NO = UC.CATEGORY_U_NO JOIN SUB_CATEGORY SC ON B.CATEGORY_S_NO = SC.CATEGORY_S_NO WHERE B.CATEGORY_U_NO = 9
AND B.CATEGORY_S_NO = #{catSNo}
AND B.TITLE LIKE #{searchPattern}
ORDER BY B.CREATE_DATE DESC

mapper.update:
UPDATE BOARD
SET CATEGORY_S_NO = #{categorySNo},
	TITLE = #{title},
	CONTENT = #{content},
	USER_NO = #{userNo}
WHERE BOARD_NO = #{boardNo}

mapper.select:
SELECT *
FROM SUB_CATEGORY
WHERE CATEGORY_U_NO = #{catUNo}

SELECT *
FROM BOARD
WHERE BOARD_NO = #{boardNo}

SELECT DATE_FORMAT(TODAY, "%m/%d") AS today, TODAY_COUNT AS todayCount
FROM TODAY_LOGIN
WHERE DAY(TODAY) BETWEEN DAY(CURDATE())-7 AND DAY(CURDATE())-1;

mapper.update:
UPDATE MEMBER
SET TODAY_LOGIN = 'N'
WHERE CURRENT_LOGIN = 'N';
