mapper.select:
SELECT COUNT(*) FROM CARPOOL JOIN LOCATION USING(LOCATION_NO) JOIN BOARD B USING(BOARD_NO) WHERE B.STATUS = 'Y'
AND CATEGORY_S_NO = #{scNo}
AND LOCATION_NO = #{location}

SELECT C.BOARD_NO, C.RECRUITS_NUM, C.RECRUITS_CURR, C.IS_END, B.TITLE, L.LOCATION_NAME, CONCAT('/resources/upfiles/carPool/', IFNULL(I.CHANGE_NAME,'NULL')) AS IMG_PATH FROM CARPOOL C LEFT JOIN BOARD B ON B.BOARD_NO = C.BOARD_NO LEFT JOIN LOCATION L ON C.LOCATION_NO = L.LOCATION_NO LEFT JOIN IMAGE I ON C.BOARD_NO = I.BOARD_NO LEFT JOIN UPPER_CATEGORY UC ON B.CATEGORY_U_NO = UC.CATEGORY_U_NO WHERE UC.CATEGORY_U_NO = 3 AND B.STATUS = 'Y' AND I.STATUS = 'Y'
AND B.CATEGORY_S_NO = #{scNo}
AND C.LOCATION_NO = #{location}

mapper.update:
UPDATE CARPOOL
SET IS_END = 'Y'
WHERE BOARD_NO = #{boardNo}
