mapper.select:
SELECT P.BOARD_NO, P.LOCATION_NO, P.PRICE, P.START_TIME, P.END_TIME, P.IS_END, B.CATEGORY_S_NO, B.TITLE, B.CONTENT, B.WISH_COUNT, L.LOCATION_NAME, P.LOCATION_NO, CONCAT('/resources/upfiles/ptj/', CHANGE_NAME) AS IMG_PATH, B.STATUS FROM PART_TIME_JOB P LEFT JOIN BOARD B ON B.BOARD_NO = P.BOARD_NO LEFT JOIN LOCATION L ON P.LOCATION_NO = L.LOCATION_NO LEFT JOIN IMAGE I ON B.BOARD_NO = I.BOARD_NO LEFT JOIN UPPER_CATEGORY UC ON B.CATEGORY_U_NO = UC.CATEGORY_U_NO WHERE UC.CATEGORY_U_NO = 5 AND B.STATUS = 'Y' AND I.STATUS = 'Y'
AND B.CATEGORY_S_NO = #{scNo}
AND P.LOCATION_NO = #{lNo}

mapper.update:
UPDATE PART_TIME_JOB
SET IS_END = 'Y'
WHERE BOARD_NO = #{boardNo}

	AND USER_NO = #{userNo}

UPDATE IMAGE
   SET STATUS = 'N'
WHERE BOARD_NO = #{boardNo}

mapper.select:
SELECT COUNT(*) FROM PART_TIME_JOB JOIN LOCATION USING(LOCATION_NO) JOIN BOARD B USING(BOARD_NO) JOIN IMAGE USING(BOARD_NO) WHERE B.STATUS = 'Y'
AND CATEGORY_S_NO = #{scNo}
AND LOCATION_NO = #{lNo}
