mapper.select:
SELECT BOARD_NO , TITLE , PRICE , TRADE_METHOD , IS_SOLDOUT , LOCATION_NAME , TITLE , PRODUCT_NAME , CONCAT('/resources/upfiles/usedProduct/', CHANGE_NAME) AS THUMB_NAIL FROM USED_PRODUCT JOIN LOCATION USING(LOCATION_NO) JOIN BOARD B USING(BOARD_NO) JOIN IMAGE USING(BOARD_NO) WHERE B.STATUS = 'Y'
AND CATEGORY_S_NO = #{scNo}
AND LOCATION_NO = #{location}
AND PRICE >= #{minPrice}
AND PRICE <= #{maxPrice}
AND TRADE_METHOD = #{tradeMethod}
AND B.CONTENT LIKE CONCAT(CONCAT('%',#{keyword}),'%') OR B.TITLE LIKE CONCAT(CONCAT('%',#{keyword}),'%')
GROUP BY BOARD_NO
ORDER BY BOARD_NO DESC
ORDER BY WISH_COUNT DESC
ORDER BY READ_COUNT DESC
ORDER BY PRICE DESC

SELECT COUNT(*)
  FROM USED_PRODUCT
  JOIN LOCATION USING(LOCATION_NO)
  JOIN BOARD B USING(BOARD_NO)
  JOIN IMAGE USING(BOARD_NO)
 WHERE B.STATUS = 'Y'

 		AND CATEGORY_S_NO = #{scNo}


 		AND LOCATION_NO = #{location}


 		AND PRICE >= #{minPrice}


 		AND PRICE <= #{maxPrice}


 		AND TRADE_METHOD = #{tradeMethod}


 		AND B.CONTENT LIKE CONCAT(CONCAT('%',#{keyword}),'%')
 		 OR B.TITLE LIKE CONCAT(CONCAT('%',#{keyword}),'%')
