mapper.insert:
INSERT INTO BOARD ( CATEGORY_S_NO, TITLE, CONTENT, USER_NO, CATEGORY_U_NO
) VALUES( 2, #{title}, #{content}, #{userNo}, 7 )
SELECT LAST_INSERT_ID()

mapper.select:
SELECT * 
FROMM BOARD b
FULL OUTER JOIN SMALL_GROUP s ON b.BOARD_NO = s.BOARD_NO;

mapper.update:
UPDATE BOARD 
SET TITLE = #{title},
   CONTENT = #{content}
 WHERE BOARD_NO = #{boardNo}

UPDATE BOARD 
   SET READ_COUNT = READ_COUNT + 1
 WHERE BOARD_NO =#{bNo}

UPDATE BOARD
   SET STATUS = 'N'
 WHERE BOARD_NO = #{boardNo}

  	AND USER_NO = #{userNo}

UPDATE BOARD
   SET
	   TITLE = #{title},
	   CONTENT = #{content}
WHERE BOARD_NO = #{boardNo}

UPDATE BOARD
 SET WISH_COUNT = WISH_COUNT + 1
 WHERE BOARD_NO = #{bNo}

UPDATE BOARD
 SET WISH_COUNT = WISH_COUNT - 1
 WHERE BOARD_NO = #{bNo}

UPDATE IMAGE
   SET STATUS = 'N'
 WHERE IMG_NO IN ${deleteImageList}
